import './App.css';
import {Box, Button, Typography} from "@mui/material";
import tubeImage from "./static/Tube.png"
import AlertDialog from "./components/dialogAlert";
import {useState} from "react";


const AlertModal = ({ message, isOpen, onClose }) => {
  return (
    <div className={`alert-modal ${isOpen ? 'open' : ''}`}>
      <div className="alert-content">
        <h3>Alert</h3>
        <p>{message}</p>
        <button onClick={onClose}>OK</button>
      </div>
    </div>
  );
};

function App() {
    const [openAlert, setOpenAlert] = useState(false)
    const message = "업데이트 요청됌.";

    const handleOpen = () => {
        console.log("버튼 클릭됌.")
        // window.alert(message)
        setOpenAlert(true)
    }

    const handleCloseAlert = () => {
    setOpenAlert(false);
  }

  return (
    <div className="App">
        <header className="App-header">
            <Box width={"80%"} height={"auto"} display={"flex"} justifyContent={"center"} alignItems={"center"}>
                <img src={tubeImage} alt={"튜브 따봉 이미지"} width={"80%"}/>
            </Box>

            <Box width={"90%"} height={"auto"} marginBottom={2}>
                <Typography fontSize={12}>
                    마지막 업데이트:
                </Typography>
                <Typography fontSize={12}>
                    2024-06-07 14시 32분
                </Typography>
            </Box>

            <Box marginBottom={2}>
                <Button variant={"contained"} onClick={handleOpen}>
                    수동 업데이트
                </Button>
            </Box>
            <AlertModal message="업데이트 요청됌." isOpen={openAlert} onClose={handleCloseAlert} />
        </header>
        {/*<AlertDialog open={openAlert} onClose={handleCloseAlert} />*/}
    </div>
  );
}

export default App;
